const img = document.getElementById("img");

function makeBig() {
  img.style.width = "35%";
}
function makeSmall() {
  img.style.width = "15%";
}
function deleteImage() {
  document.getElementById("img").parentNode.removeChild(img);
}

function addImage() {
  if (!document.getElementById("img")) {
    var image = document.createElement("img");
    image.id = "img";
    image.src = "images.jpg";
    document.body.appendChild(image);
  }
}

function Image() {
  return (
    <div>
      <button onClick={addImage}>Додати</button>
      <button onClick={makeBig}>Збільшити</button>
      <button onClick={makeSmall}>Зменшити</button>
      <button onClick={deleteImage}>Видалити</button>

      <img id="img" src="images.jpg" />
    </div>
  );
}
export default Image;
